import React from "react";
import "./About.css";
import { motion } from "framer-motion";

const description = "I am a professional who graduated";
const words = description.split(" ");
const nameArr = words
  .slice(
    0,
    words.findIndex((word) => word.includes(" "))
  )
  .join(" dsd");
console.log(nameArr);

const transition = {
  ease: "easeInOut",
};

const firstExampleVariant = {
  animate: (i) => ({
    transition: {
      delayChildren: 0.6,
      staggerChildren: 0.18,
      staggerDirection: i,
    },
  }),
};

const firstExampleLetterVariant = {
  initial: { opacity: 0, y: "120%" },
  animate: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.4,
      ...transition,
    },
  },
};

const About = () => {
  return (
    <div className="box-text">
      <motion.h1
        variants={firstExampleVariant}
        initial="initial"
        animate="animate"
        className="firstExample"
      >
        {nameArr.split("").map((letter, index) => (
          <motion.span
            key={index}
            variants={firstExampleLetterVariant}
            custom={1} // we can remove it if we declare a staggerDirection of 1 or -1 !
          >
            {letter === " " ? " " : letter}
            {/* Add a space character if the letter is a space */}
          </motion.span>
        ))}
      </motion.h1>
    </div>
  );
};

export default About;
