import React, { useEffect, useRef } from "react";
import "./About.css";

const About = () => {
  const splittingRef = useRef(null);

  useEffect(() => {
    const target = document.querySelector("#target");
    const Splitting = require("splitting");
    splittingRef.current = Splitting({ target: target, by: "lines" });
  }, []);

  return (
    <div>
      <div
        id="target"
        className="splitting lines"
        style={{ "--line-total": 1, "--word-total": 2 }}
      >
        <span
          className="word"
          data-word="Splitting"
          style={{ "--word-index": 0 }}
        >
          Splitting
        </span>
        <span className="word" data-word="Text" style={{ "--word-index": 1 }}>
          Text
        </span>
      </div>
    </div>
  );
};

export default About;
