export default About;
import React from "react";
import "./styles.css";
import { motion } from "framer-motion";

const About = () => {
  const name = "MARINA";
  const nameArr = name.split("");

  const transition = {
    ease: [0.5, 0.01, -0.05, 0.9],
  };

  const firstExampleVariant = {
    animate: (i) => ({
      transition: {
        delayChildren: 0.6,
        staggerChildren: 0.18,
        staggerDirection: i, //we can replace it with custom number so that custom props doesn't need to declared!
      },
    }),
  };
  const secondExampleVariant = {
    animate: {
      transition: {
        delayChildren: 0.6,
        staggerChildren: 0.04,
        staggerDirection: -1,
      },
    },
  };

  const firstExampleLetterVariant = {
    initial: { opacity: 0, y: "120%" },
    animate: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.4,
        ...transition,
      },
    },
  };

  const secondExampleLetterVariant = {
    initial: {
      y: 400,
    },
    animate: {
      y: 0,
      transition: { duration: 1, ...transition },
    },
  };

  return (
    <div className="App">
      <motion.h1
        variants={firstExampleVariant}
        initial="initial"
        animate="animate"
        className="firstExample"
      >
        {nameArr.map((letter, index) => (
          <motion.span
            key={index}
            variants={firstExampleLetterVariant}
            custom={1} // we can remove it if we declare a staggerDirection of 1 or -1 !
          >
            {letter}
          </motion.span>
        ))}
      </motion.h1>
      <motion.h1
        variants={secondExampleVariant}
        initial="initial"
        animate="animate"
        className="secondExample"
      >
        <motion.span variants={secondExampleLetterVariant}>M</motion.span>
        <motion.span variants={secondExampleLetterVariant}>A</motion.span>
        <motion.span variants={secondExampleLetterVariant}>R</motion.span>
        <motion.span variants={secondExampleLetterVariant}>I</motion.span>
        <motion.span variants={secondExampleLetterVariant}>N</motion.span>
        <motion.span variants={secondExampleLetterVariant}>A</motion.span>
      </motion.h1>
    </div>
  );
};
