import React, { useState, useEffect } from "react";
import "./About.css";

const About = () => {
  const [text, setText] = useState("".padEnd(fullText.length, " ")); // Initialize with empty spaces

  const fullText = "Hi there, what's your name?";

  useEffect(() => {
    let currentIndex = 0;
    const interval = setInterval(() => {
      setText(
        (prevText) => prevText.slice(0, currentIndex) + fullText[currentIndex]
      );
      currentIndex++;

      if (currentIndex === fullText.length) {
        clearInterval(interval);
      }
    }, 100);

    return () => {
      clearInterval(interval);
    };
  }, []);

  return (
    <div className="box-text">
      <h1 className="firstExample">{text}</h1>
    </div>
  );
};

export default About;
