// export default About;
import React, { useState, useEffect } from "react";
import "./About.css";

const About = () => {
  const [text, setText] = useState("");
  const [scale, setScale] = useState(1);
  const [wordCount, setWordCount] = useState(0);

  const fullText = "Hi there, what's your name?";

  useEffect(() => {
    let currentIndex = 0;
    const interval = setInterval(() => {
      setText((prevText) => prevText + fullText[currentIndex]);
      currentIndex++;
      if (currentIndex === fullText.length - 1) {
        clearInterval(interval);
        setTimeout(() => {
          setScale(1.5);
          setTimeout(() => {
            setScale(1);
          }, 300);
        }, 300);
      }
    }, 100);

    // Count words when text changes
    const words = text.split(" ");
    setWordCount(words.length);

    return () => {
      clearInterval(interval);
    };
  }, [text]);

  // Apply scale transformation to each split item
  const splitText = text.split(" ").map((item, index) => (
    <span
      key={index}
      style={{ transform: `scale(${scale})`, transition: "0.3s" }}
    >
      {item}
    </span>
  ));

  return (
    <div className="box-text">
      <h1 className="firstExample" style={{ color: "green" }}>
        {splitText}
      </h1>
      <p>Word Count: {wordCount}</p>
    </div>
  );
};

export default About;
