import React, { useState, useEffect } from "react";
import "./About.css";

const About = () => {
  const [text, setText] = useState("");
  const fullText = "Hi there, what's your name?";

  useEffect(() => {
    let currentIndex = 0;
    const interval = setInterval(() => {
      setText((prevText) => prevText + fullText[currentIndex]);
      currentIndex++;
      if (currentIndex === fullText.length - 1) {
        clearInterval(interval);
        // setTimeout(() => {
        //   setText((prevText) => prevText + fullText[currentIndex]);
        // }, 1000);
      }
    }, 100);

    return () => {
      clearInterval(interval);
    };
  }, []);

  useEffect(() => {
    console.log(text); // Move the console.log inside a separate useEffect
  }, [text]);

  const words = text.split(" "); // Split the text into words

  return (
    <div className="box-text">
      <h1 className="firstExample">
        {words.map((word, index) => (
          <span
            key={index}
            style={{
              color: "green",
              transitionDelay: `${index * 100}ms`,
              transition: "color 0.5s",
            }}
            onAnimationEnd={() => {
              if (index > 0) {
                const prevWord = document.getElementById(`word-${index - 1}`);
                prevWord.style.color = "white";
              }
            }}
            id={`word-${index}`}
          >
            {word + " "}
          </span>
        ))}
      </h1>
    </div>
  );
};

export default About;
