import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { SplitTextPlugin } from "gsap/SplitText";
import "./About.css";

const About = () => {
  const quoteRef = useRef(null);
  const splitTextTimeline = useRef(null);
  const mySplitText = useRef(null);

  useEffect(() => {
    gsap.registerPlugin(SplitTextPlugin);
    const $quote = quoteRef.current;
    mySplitText.current = new SplitText($quote, { type: "words" });
    splitTextTimeline.current = gsap.timeline();
    gsap.set($quote, { perspective: 400 });
  }, []);

  const kill = () => {
    splitTextTimeline.current.clear().time(0);
    mySplitText.current.revert();
  };

  const handleCharsClick = () => {
    kill();
    mySplitText.current.split({ type: "chars, words" });
    splitTextTimeline.current.from(mySplitText.current.chars, {
      duration: 0.6,
      scale: 4,
      autoAlpha: 0,
      rotationX: -180,
      transformOrigin: "100% 50%",
      ease: "back",
      stagger: 0.02,
    });
  };

  const handleWordsClick = () => {
    kill();
    mySplitText.current.split({ type: "words" });
    mySplitText.current.words.forEach((el, index) => {
      splitTextTimeline.current.from(
        el,
        { duration: 0.6, opacity: 0, force3D: true },
        index * 0.01
      );
      splitTextTimeline.current.from(
        el,
        { duration: 0.6, scale: index % 2 === 0 ? 0 : 2 },
        index * 0.01
      );
    });
  };

  const handleLinesClick = () => {
    kill();
    mySplitText.current.split({ type: "lines" });
    splitTextTimeline.current.from(mySplitText.current.lines, {
      duration: 0.5,
      opacity: 0,
      rotationX: -120,
      force3D: true,
      transformOrigin: "top center -150",
      stagger: 0.1,
    });
  };

  const handleCharsWordsLinesClick = () => {
    kill();
    mySplitText.current.split({ type: "chars, words, lines" });
    splitTextTimeline.current
      .from(
        mySplitText.current.chars,
        { duration: 0.2, autoAlpha: 0, scale: 4, force3D: true, stagger: 0.01 },
        0.5
      )
      .to(
        mySplitText.current.words,
        { duration: 0.1, color: "#8FE402", scale: 0.9, stagger: 0.1 },
        "words"
      )
      .to(
        mySplitText.current.words,
        { duration: 0.2, color: "white", scale: 1, stagger: 0.1 },
        "words+=0.1"
      )
      .to(mySplitText.current.lines, {
        duration: 0.5,
        x: 100,
        autoAlpha: 0,
        stagger: 0.2,
      });
  };

  const handleRevertClick = () => {
    mySplitText.current.revert();
  };

  return (
    <div>
      <div id="nav">
        <button id="chars" onClick={handleCharsClick}>
          chars
        </button>
        <button id="words" onClick={handleWordsClick}>
          words
        </button>
        <button id="lines" onClick={handleLinesClick}>
          lines
        </button>
        <button id="charsWordsLines" onClick={handleCharsWordsLinesClick}>
          chars words and lines
        </button>
        <button id="revert" onClick={handleRevertClick}>
          revert
        </button>
      </div>

      <div id="demo">
        <div id="quote" ref={quoteRef}>
          This is a test that i am trying to ljsn dlsakjnlasj kalkjndasj
          kljanodjksa nadokdan lan odan
        </div>
      </div>
    </div>
  );
};

export default About;
