import React, { useEffect, useState } from "react";
import { gsap } from "gsap";
import { CSSPlugin } from "gsap/CSSPlugin";
import "./Work.css";
import tinDog from "./Images/Tindog.png";
import space from "./Images/space.png";

const Work = () => {
  useEffect(() => {
    gsap.registerPlugin(CSSPlugin);
  }, []);

  const [items, setItems] = useState([
    {
      background:
        "https://c4.wallpaperflare.com/wallpaper/815/711/721/anime-boy-fight-knife-wallpaper-preview.jpg",
      clicked: false,
    },
    {
      background: tinDog,
      clicked: false,
    },
    {
      background: space,
      clicked: false,
    },
    // {
    //   background:
    //     "https://rare-gallery.com/thumbs/4512738-black-eyes-guweiz-short-hair-armor-black-hair-gloves-headdress-japanese-clothes-rain-samurai-umbrella-water.jpg",
    //   clicked: false,
    // },
    // {
    //   background:
    //     "https://i.pinimg.com/originals/d8/ef/da/d8efda191c6736020d29fd4780204e15.jpg",
    //   clicked: false,
    // },
  ]);

  const expand = (index) => {
    setItems((prevItems) =>
      prevItems.map((item, i) => ({
        ...item,
        clicked: i === index ? !item.clicked : false,
      }))
    );
  };

  const handleItemClick = (index) => {
    expand(index);
  };

  useEffect(() => {
    gsap.to(".item", {
      duration: 2,
      ease: "elastic.out(1, 0.3)",
      width: (index) => (items[index].clicked ? "30vw" : "10vw"),
      className: (index) => (items[index].clicked ? "item-active" : "item"),
    });
  }, [items]);

  return (
    <div className="group">
      {items.map((item, index) => (
        <div
          key={index}
          className="item"
          style={{
            backgroundImage: `url(${item.background})`,
          }}
          onClick={() => handleItemClick(index)}
        ></div>
      ))}
    </div>
  );
};

export default Work;
