import React from "react";
import "./About.css";
import { motion } from "framer-motion";

const description = "ZION";
const nameArr = description.split("");
console.log(nameArr);

const transition = {
  ease: "easeInOut",
};

const firstExampleVariant = {
  animate: (i) => ({
    transition: {
      delayChildren: 0.6,
      staggerChildren: 0.18,
      staggerDirection: i,
    },
  }),
};

const firstExampleLetterVariant = {
  initial: { opacity: 0, y: "120%" },
  animate: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.4,
      ...transition,
    },
  },
};

const About = () => {
  return (
    <div className="box-text">
      <motion.h1
        variants={firstExampleVariant}
        initial="initial"
        animate="animate"
        className="firstExample"
      >
        {nameArr.map((letter, index) => (
          <motion.span
            key={index}
            variants={firstExampleLetterVariant}
            custom={1} // we can remove it if we declare a staggerDirection of 1 or -1 !
          >
            {letter}
          </motion.span>
        ))}
      </motion.h1>
    </div>
  );
};
export default About;
