import React, { useEffect, useState } from "react";
import "./Menu.css";
import Work from "./Contents/Work";
import About from "./Contents/About";

const Menu = () => {
  useEffect(() => {
    const handleMouseMove = (e) => {
      const $menu = document.querySelector(".Menu-list");
      const $item = document.querySelectorAll(".Menu-list-item");
      const w = window.innerWidth;
      const h = window.innerHeight;

      const offsetX = 0.5 - e.pageX / w;
      const offsetY = 0.5 - e.pageY / h;
      const dy = e.pageY - h / 4;
      const dx = e.pageX - w / 4;
      const theta = Math.atan2(dy, dx);
      let angle = (theta * 180) / Math.PI - 90;

      if (angle < 0) {
        angle += 360;
      }

      const offsetPoster = $menu.dataset.offset;
      const transformPoster = `translate3d(0, ${
        -offsetX * offsetPoster
      }px, 0) rotateX(${-offsetY * offsetPoster}deg) rotateY(${
        offsetX * (offsetPoster * 3)
      }deg)`;

      $menu.style.transform = transformPoster;

      $item.forEach((item) => {
        const offsetLayer = item.dataset.offset || 0;
        const transformLayer = `translate3d(${offsetX * offsetLayer}px, ${
          offsetY * offsetLayer
        }px, 20px)`;

        item.style.transform = transformLayer;
      });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);
  const [classes, setClasses] = useState("");
  const [work, setWork] = useState(false);
  const workHandler = () => {
    if (!work) {
      setClasses("group-effect-show ");
      setWork(true);
      return;
    }
    if (work) {
      setClasses("group-effect-hide");
      setTimeout(function () {
        setWork(false);
      }, 1000);

      return;
    }
  };
  const aboutHandler = () => {
    if (!work) {
      setClasses("group-effect-show ");
      setWork(true);
      return;
    }
    if (work) {
      setClasses("group-effect-hide");
      setTimeout(function () {
        setWork(false);
      }, 1000);

      return;
    }
  };

  return (
    <div class="Menu">
      <ul class="Menu-list" data-offset="10">
        <li
          class="Menu-list-item"
          data-offset="16"
          onClick={AboutHandler}
          onclick
        >
          About
          <span class="Mask">
            <span>About</span>
          </span>
          <span class="Mask">
            <span>About</span>
          </span>
        </li>
        <About />
        <li
          onClick={workHandler}
          class="Menu-list-item"
          data-offset="12"
          onclick
        >
          Work
          <span class="Mask">
            <span>Work</span>
          </span>
          <span class="Mask">
            <span>Work</span>
          </span>
        </li>
        <div className={work ? classes : ""}> {work && <Work />}</div>

        <li class="Menu-list-item" data-offset="8" onclick>
          Contact
          <span class="Mask">
            <span>Contact</span>
          </span>
          <span class="Mask">
            <span>Contact</span>
          </span>
        </li>
      </ul>
    </div>
  );
};

export default Menu;
