import React, { useEffect } from "react";

const About = () => {
  useEffect(() => {
    const target = document.querySelector(".target");
    const strText = target.textContent;
    const splitText = strText.split("");
    target.textContent = "";

    for (let i = 0; i < splitText.length; i++) {
      target.innerHTML += "<span class='char'>" + splitText[i] + "</span>";
    }

    let char = 0;
    let timer = setInterval(onTick, 60);

    function onTick() {
      const span = target.querySelectorAll(".char")[char];
      span.classList.add("Fadein");
      char++;

      if (char === splitText.length) {
        complete();
        return;
      }
    }

    function complete() {
      clearInterval(timer);
      timer = null;
    }

    return () => {
      clearInterval(timer);
    };
  }, []);

  return (
    <div className="box-text">
      <h1 className="target">Hello World</h1>
    </div>
  );
};

export default About;
