import React, { useEffect, useState } from "react";
import { gsap } from "gsap";
import { CSSPlugin } from "gsap/CSSPlugin";
import "./Work.css";
import tinDog from "./Images/Tindog.png";
import space from "./Images/space.png";
const itemsData = [
  {
    title: "Item 1",
    content: "Content for Item 1",
    button: "Button 1",
    clicked: false,
    active: false,
  },
  {
    title: "Item 2",
    content: "Content for Item 2",
    button: "Button 2",
    clicked: false,
    active: false,
  },
  {
    title: "Item 3",
    content: "Content for Item 3",
    button: "Button 3",
    clicked: false,
    active: false,
  },
];

const Work = (props) => {
  useEffect(() => {
    gsap.registerPlugin(CSSPlugin);
  }, []);

  const [items, setItems] = useState(itemsData);

  const expand = (index) => {
    setItems((prevItems) =>
      prevItems.map((item, i) => ({
        ...item,
        clicked: i === index ? !item.clicked : false,
      }))
    );
  };

  const handleItemClick = (index) => {
    expand(index);
    setTimeout(() => {
      setItems((prevItems) =>
        prevItems.map((item, i) => ({
          ...item,
          active: i === index && item.clicked,
        }))
      );
    }, 1000);
  };

  useEffect(() => {
    gsap.to(".item", {
      duration: 2,
      ease: "elastic.out(1, 0.3)",
      width: (index) => (items[index].clicked ? "30vw" : "10vw"),
      className: (index) => (items[index].active ? "item item-active" : "item"),
    });
  }, [items]);
  useEffect(() => {
    gsap.to(".item", {
      duration: 2,
      ease: "elastic.out(1, 0.3)",
      width: (index) => (items[index].clicked ? "30vw" : "10vw"),
      className: (index) =>
        items[index].clicked ? "item item-active" : "item",
    });
  }, [items]);

  return (
    <div className="container">
      {items.map((item, index) => (
        <div
          className="item"
          key={index}
          onClick={() => handleItemClick(index)}
        >
          <div className="content-div">
            <h2 className={item.active ? "title active" : "title"}>
              {item.title}
            </h2>
            <p className={item.active ? "copy active" : "copy"}>
              {item.content}
            </p>
            <button
              type="button"
              className={item.active ? "btn active" : "btn"}
            >
              {item.button}
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Work;
