import React, { useState, useEffect } from "react";
import "./About.css";

const About = () => {
  const [text, setText] = useState("");

  const fullText = `  I am a professional who graduated in Computer Networks and have 06 years of experience in customer service. Throughout my career, I have worked in small and medium-sized companies, but I am currently transitioning into the field of Information Technology, specifically focusing on front-end programming.
 My expertise lies in programming logic, code development, and creating robust systems with high quality. I prioritize code readability and have experience in layout development, website and system maintenance, as well as ensuring compliance with regulations. Additionally, I specialize in optimizing web pages for performance, scalability, and providing the best user experience.
 I possess the necessary skills to develop interfaces for web system projects using HTML, CSS, Bootstrap, WordPress, JavaScript, jQuery, Git, and GitHub technologies. My main focus is on front-end development, where I excel at translating creative designs into clean and bug-free code. I am dedicated to delivering results that meet and exceed expectations.`;

  useEffect(() => {
    let currentIndex = 0;
    const interval = setInterval(() => {
      setText((prevText) => prevText + fullText[currentIndex]);
      currentIndex++;
      if (currentIndex === fullText.length - 1) {
        clearInterval(interval);
      }
    }, 100);
  }, []);

  return (
    <div className="box-text">
      <p className="firstExample">{text}</p>
    </div>
  );
};

export default About;
