import React, { useState, useEffect } from "react";
import { gsap } from "gsap";
import CSSPlugin from "gsap/CSSPlugin";
import "./Work.css";

const Work = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    gsap.registerPlugin(CSSPlugin);
    const items = document.querySelectorAll(".item");
    gsap.to(items, {
      width: "30vw",
      duration: 2.5,
      ease: "elastic(1, .3)",
    });
  }, []);

  const expand = (item, i, clickedClass) => {
    const updatedItems = items.map((it, ind) => {
      if (i === ind) return { ...it, clicked: !it.clicked, clickedClass };
      return { ...it, clicked: false, clickedClass: "" };
    });

    gsap.to(updatedItems, {
      width: item.clicked ? "10vw" : "3vw",
      duration: 2,
      ease: "elastic(1, .6)",
    });

    gsap.to(item, {
      width: item.clicked ? "30vw" : "10vw",
      duration: 2.5,
      ease: "elastic(1, .3)",
    });

    setItems(updatedItems);
  };

  const handleItemClick = (item, i) => {
    expand(item, i, item.clicked ? "" : "clicked");
  };

  const itemList = [
    {
      background:
        "https://c4.wallpaperflare.com/wallpaper/815/711/721/anime-boy-fight-knife-wallpaper-preview.jpg",
      clicked: false,
      clickedClass: "",
    },
    {
      background:
        "https://w0.peakpx.com/wallpaper/913/450/HD-wallpaper-senjin-female-dress-sexy-warrior-girl-blade-emotional-anime-hot-petals-anime-girl-long-hair-sword-serious.jpg",
      clicked: false,
      clickedClass: "",
    },
    {
      background:
        "https://e0.pxfuel.com/wallpapers/182/524/desktop-wallpaper-game-application-digital-art-women-samurai-warrior-anime-samurai-art.jpg",
      clicked: false,
      clickedClass: "",
    },
    {
      background:
        "https://rare-gallery.com/thumbs/4512738-black-eyes-guweiz-short-hair-armor-black-hair-gloves-headdress-japanese-clothes-rain-samurai-umbrella-water.jpg",
      clicked: false,
      clickedClass: "",
    },
    {
      background:
        "https://i.pinimg.com/originals/d8/ef/da/d8efda191c6736020d29fd4780204e15.jpg",
      clicked: false,
      clickedClass: "",
    },
  ];

  return (
    <div className="group">
      {itemList.map((item, i) => (
        <div
          key={i}
          className={`item ${item.clickedClass}`}
          style={{
            backgroundImage: `url(${item.background})`,
            width: item.clicked ? "30vw" : "10vw",
          }}
          onClick={() => handleItemClick(item, i)}
        ></div>
      ))}
    </div>
  );
};

export default Work;
