import React, { useState, useEffect } from "react";
import "./About.css";

const About = () => {
  const [text, setText] = useState("");
  const fullText = "Hi there, what's your name?";

  useEffect(() => {
    let currentIndex = 0;
    const interval = setInterval(() => {
      setText((prevText) => prevText + fullText[currentIndex]);
      currentIndex++;
      if (currentIndex === fullText.length - 1) {
        clearInterval(interval);
        clearInterval(interval);
      }
    }, 100);

    return () => {
      clearInterval(interval);
      setTimeout(() => {
        setText("");
      }, 1000);
    };
  }, []);

  useEffect(() => {
    console.log(text); // Move the console.log inside a separate useEffect
  }, [text]);

  return (
    <div className="box-text">
      <h1 className="firstExample" style={{ color: "green" }}>
        {text}
      </h1>
    </div>
  );
};

export default About;
