import React, { useEffect, useRef } from "react";

const About = () => {
  const targetRef = useRef(null);

  useEffect(() => {
    const target = targetRef.current;
    const strText = target.textContent;
    const splitText = strText.split("");
    target.textContent = "";
    splitText.forEach((char) => {
      const charSpan = document.createElement("span");
      charSpan.className = "char";
      charSpan.textContent = char;
      target.appendChild(charSpan);
    });

    let char = 0;
    let timer = setInterval(onTick, 60);

    function onTick() {
      const span = target.querySelectorAll(".char")[char];
      span.classList.add("Fadein");
      char++;
      if (char === splitText.length) {
        complete();
        return;
      }
    }

    function complete() {
      clearInterval(timer);
      timer = null;
    }

    return () => {
      clearInterval(timer);
    };
  }, []);

  return (
    <div>
      <h1 ref={targetRef} className="target">
        Hello World
      </h1>
    </div>
  );
};

export default About;
