import React, { useEffect, useRef } from "react";

const SplitText = ({ className, children, transition, animation }) => {
  const spanRef = useRef(null);

  useEffect(() => {
    const chars = children.split("");
    const span = spanRef.current;

    span.innerHTML = chars.reduce((acc, curr) => {
      if (curr === "＆") {
        curr = curr.replace("＆", "&amp;");
      } else if (curr === /\s+/) {
        curr = curr.replace(/\s+/, "&nbsp;");
      }

      return `${acc}<span class="char" aria-hidden="true">${curr}</span>`;
    }, "");

    const charEls = span.querySelectorAll(".char");

    charEls.forEach((charEl, index) => {
      let transitionDelayTime;
      let animationDelayTime;

      if (transition) {
        transitionDelayTime = Math.round(transition[0] * index) / 1000 + "s";
      }

      if (animation) {
        animationDelayTime = Math.round(animation[0] * index) / 1000 + "s";
      }

      if (animation && transition) {
        charEl.style.cssText = `animation-delay: ${animationDelayTime}; transition-delay: ${transitionDelayTime};`;
      } else if (animation) {
        charEl.style.cssText = `animation-delay: ${animationDelayTime};`;
      } else if (transition) {
        charEl.style.cssText = `transition-delay: ${transitionDelayTime};`;
      }
    });
  }, [children, transition, animation]);

  return <span ref={spanRef} className={className} />;
};

const About = () => {
  return (
    <main id="main">
      <div id="text" className="js-split-block">
        <SplitText className="js-split-text" animation={[32, 42]}>
          Test Text
        </SplitText>

        <SplitText className="js-split-text" animation={[32, 42]}>
          Test Text02
        </SplitText>

        <SplitText className="js-split-text" animation={[32, 42]}>
          Test Text03
        </SplitText>
      </div>
    </main>
  );
};

export default About;
