import React, { useState, useEffect } from "react";
import "./About.css";

const About = () => {
  const [text, setText] = useState("");
  const [scale, setScale] = useState(1);
  const [wordCount, setWordCount] = useState(0); // Add wordCount state

  const fullText = "Hi there, what's your name?";

  useEffect(() => {
    let currentIndex = 0;
    const interval = setInterval(() => {
      setText((prevText) => prevText + fullText[currentIndex]);
      currentIndex++;
      if (currentIndex === fullText.length - 1) {
        const words = text.split(" ");
        setWordCount(words.length);
        console.log(wordCount);
      }
    }, 100);

    return () => {
      clearInterval(interval);
    };
  }, []); // Add text as a dependency

  return (
    <div className="box-text">
      <h1
        className="firstExample"
        style={{ color: "green", transform: `scale(${scale})` }}
      >
        {text}
      </h1>
      <p>Word Count: {wordCount}</p> {/* Display word count */}
    </div>
  );
};

export default About;
